<?php abstract class Controller{protected $databasePath;public function __construct(){$this->databasePath="model/Database.php";}public function display(){$page=$_GET['action']."Display";$this->$page();}protected function extensionOk($imageName){$extensionIsOk=false;$ext=pathinfo($imageName,PATHINFO_EXTENSION);switch($ext){case "PNG":case "png":case "JPG":case "jpg":case "GIF":case "gif":$extensionIsOk=true;break;default:$extensionIsOk=false;break;}return $extensionIsOk;}}